#Create Object
soil = VoltageRatioInput()
pump = DigitalOutput()
light = LightSensor()
lcd = LCD()


#Address
pump.setHubPort(1)
pump.setIsHubPortDevice(True)

#Open
pump.openWaitForAttachment(1000)
soil.openWaitForAttachment(1000)
light.openWaitForAttachment(1000)
lcd.openWaitForAttachment(1000)

#Use your Phidgets
#Configure LCD
lcd.setBacklight(0.25)
lcd.writeBitmap(64, 0, 64, 64, plant)

def checkMoisture():
    checkSoil = soil.getVoltageRatio()
    if checkSoil < 0.95:
        pump.setState(True)
        time.sleep(5)
        pump.setState(False)

while(True):
    checkMoisture()
    lightString = "Light: " + str(light.getIlluminance()) + "lx"
    soilString = "Soil: " + str(soil.getVoltageRatio())
    lcd.writeText(LCDFont.FONT_5x8, 0, 22, lightString)
    lcd.writeText(LCDFont.FONT_5x8, 0, 32, soilString)
    lcd.flush()
    time.sleep(300.0)
  
